// ══════════════════════════════════════════════════════
// Parsear TODOS los PDFs y devolver UN SOLO item con array
// Esto permite leer la Sheet 1 sola vez
// ══════════════════════════════════════════════════════

const response = $input.first().json;
const files = response.value || [];

const ESTADO_MAP = {
  'CITADOS': 'CITADO',
  'DESCARTADOS': 'DESCARTADO',
  'CITAS FUERA JORNADA': 'CITA_FUERA_JORNADA'
};

function normalizeName(str) {
  return (str || '')
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .toLowerCase()
    .replace(/\s+/g, ' ')
    .trim();
}

const pdfs = [];

for (const file of files) {
  if (!file.name || !file.name.toLowerCase().endsWith('.pdf')) continue;

  const parentPath = file.parentReference?.path || '';
  const fullPath = parentPath + '/' + file.name;

  const year = new Date().getFullYear().toString();
  if (!parentPath.includes('Base de datos')) continue;
  const afterBase = parentPath.split('/' + year + '/');
  if (afterBase.length < 2) continue;

  const pathParts = afterBase[1].split('/');
  if (pathParts.length < 4) continue;

  const posicion = pathParts[0] || '';
  const ciudad = pathParts[1] || '';
  const mes = pathParts[2] || '';
  const semana = pathParts[3] || '';
  const subcarpeta = pathParts[4] || '';

  let estado = 'PENDIENTE';
  for (const [carpeta, est] of Object.entries(ESTADO_MAP)) {
    if (subcarpeta.toUpperCase().includes(carpeta.toUpperCase())) {
      estado = est;
      break;
    }
  }

  let nombreArchivo = file.name
    .replace(/\.pdf$/i, '')
    .replace(/^CV[_\s-]*/i, '')
    .replace(/[_-]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  const nombreFormateado = nombreArchivo
    .replace(/\b\w/g, c => c.toUpperCase());

  pdfs.push({
    fileId: file.id,
    fileName: file.name,
    filePath: fullPath,
    fileSize: file.size,
    lastModified: file.lastModifiedDateTime,
    webUrl: file.webUrl || '',
    posicion, ciudad, mes, semana, subcarpeta, estado,
    nombreArchivo: nombreFormateado,
    nombreNormalizado: normalizeName(nombreFormateado)
  });
}

// Devolver UN SOLO item con el array de PDFs
return [{ json: { pdfs, totalPdfs: pdfs.length, empty: pdfs.length === 0 } }];

// ══════════════════════════════════════════════════════
// COMPARAR PDFs vs Sheet - BIDIRECCIONAL
// Detecta cambios en OneDrive Y en la Sheet
// Usa _ultimo_sync para saber quien cambio
// ══════════════════════════════════════════════════════

function norm(str) {
  return (str || '')
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .toLowerCase()
    .replace(/\s+/g, ' ')
    .trim();
}

const sheetRows = $('3. Leer Sheet COMPLETA').all().map(i => i.json);

// Indices
const sheetByName = {};
for (let i = 0; i < sheetRows.length; i++) {
  const row = sheetRows[i];
  const key = norm(row.NOMBRE || '');
  if (key && !sheetByName[key]) {
    sheetByName[key] = { ...row, _rowIndex: i + 2 };
  }
}

const pdfs = $('2. Parsear Rutas').first().json.pdfs || [];
const results = [];
let stats = { insertar: 0, actualizar_sheet: 0, mover_onedrive: 0, skip: 0 };

for (const pdf of pdfs) {
  const match = sheetByName[pdf.nombreNormalizado] || null;

  if (!match) {
    // Nuevo: no existe en Sheet → INSERTAR
    results.push({ json: { ...pdf, accion: 'INSERTAR' }});
    stats.insertar++;
    continue;
  }

  const sheetEstado = norm(match['SITUACIÓN'] || match['SITUACION'] || '');
  const onedriveEstado = norm(pdf.estado);
  const ultimoSync = norm(match._ultimo_sync || '');

  if (sheetEstado === onedriveEstado) {
    // Sincronizados → SKIP
    stats.skip++;
    continue;
  }

  // HAY DIFERENCIA: determinar quien cambio
  if (!ultimoSync || ultimoSync === sheetEstado) {
    // _ultimo_sync coincide con Sheet (o no existe) → OneDrive cambio
    // Accion: actualizar Sheet para reflejar OneDrive
    results.push({ json: {
      ...pdf,
      accion: 'ACTUALIZAR_SHEET',
      situacionAnterior: match['SITUACIÓN'] || match['SITUACION'] || '',
      matchNombre: match.NOMBRE,
      matchRowIndex: match._rowIndex
    }});
    stats.actualizar_sheet++;
  } else if (ultimoSync === onedriveEstado) {
    // _ultimo_sync coincide con OneDrive → Sheet cambio
    // Accion: mover archivo en OneDrive para reflejar Sheet
    const nuevoEstado = (match['SITUACIÓN'] || match['SITUACION'] || '').trim();
    results.push({ json: {
      ...pdf,
      accion: 'MOVER_ONEDRIVE',
      nuevoEstado: nuevoEstado,
      situacionAnterior: pdf.estado,
      matchNombre: match.NOMBRE,
      matchRowIndex: match._rowIndex
    }});
    stats.mover_onedrive++;
  } else {
    // Conflicto: ambos cambiaron. Sheet gana (es donde trabajan los usuarios)
    const nuevoEstado = (match['SITUACIÓN'] || match['SITUACION'] || '').trim();
    results.push({ json: {
      ...pdf,
      accion: 'MOVER_ONEDRIVE',
      nuevoEstado: nuevoEstado,
      situacionAnterior: pdf.estado,
      matchNombre: match.NOMBRE,
      matchRowIndex: match._rowIndex,
      _conflicto: true
    }});
    stats.mover_onedrive++;
  }
}

if (results.length === 0) {
  return [{ json: { accion: 'NINGUNA', _skip: true, _resumen: JSON.stringify(stats) }}];
}

results[0].json._resumen = JSON.stringify(stats);
return results;

// Preparar actualizacion de SITUACION + _ultimo_sync
const item = $input.first().json;

return [{ json: {
  NOMBRE: item.matchNombre || item.nombreArchivo,
  'SITUACIÓN': item.estado,
  _ultimo_sync: item.estado,
  _accion: 'ACTUALIZAR_SHEET',
  _situacionAnterior: item.situacionAnterior,
  _fileName: item.fileName
} }];

// ══════════════════════════════════════════════════════
// NODO UNIFICADO: Descargar PDF + Extraer IA + Preparar fila
// Un solo Code node para evitar paired item issues de n8n
// ══════════════════════════════════════════════════════

const rutaData = $input.first().json;
const API_KEY = $vars.OPENAI_API_KEY; // ← CAMBIAR POR TU API KEY

// --- 1. Descargar PDF de OneDrive ---
let pdfBase64 = '';
try {
  const downloadResp = await this.helpers.requestWithAuthentication.call(
    this, 'microsoftOneDriveOAuth2Api', {
      method: 'GET',
      uri: 'https://graph.microsoft.com/v1.0/me/drive/items/' + rutaData.fileId + '/content',
      encoding: null,
      resolveWithFullResponse: true
    }
  );
  pdfBase64 = Buffer.from(downloadResp.body).toString('base64');
} catch (e) {
  // Si falla la descarga, continuamos solo con el nombre del archivo
  console.log('Download failed:', e.message);
}

// --- 2. Llamar GPT-4o via OpenAI ---
let cv = {};
try {
  const userContent = pdfBase64
    ? [
        { type: 'file', file: { filename: rutaData.fileName, file_data: 'data:application/pdf;base64,' + pdfBase64 } },
        { type: 'text', text: 'Extrae los datos de este CV de InfoJobs.' }
      ]
    : 'Extrae los datos de este CV basandote en el nombre del archivo: ' + rutaData.fileName + '. Solo tienes el nombre del archivo, extrae lo que puedas.';

  const aiResp = await this.helpers.httpRequest({
    method: 'POST',
    url: 'https://api.openai.com/v1/chat/completions',
    headers: { 'Authorization': 'Bearer ' + API_KEY, 'Content-Type': 'application/json' },
    body: {
      model: 'gpt-4o',
      messages: [
        {
          role: 'system',
          content: `Eres un extractor de datos de CVs de InfoJobs para una empresa inmobiliaria. Extrae los datos del candidato y responde UNICAMENTE con JSON valido (sin markdown, sin backticks). Campos:
{
  "nombre_completo": "Nombre y apellidos exactos",
  "email": "email o vacio",
  "telefono": "telefono con prefijo o vacio",
  "residencia": "ciudad o barrio de residencia o vacio",
  "edad": "numero o vacio",
  "experiencia_resumen": "max 50 palabras",
  "formacion": "nivel formativo principal",
  "disponibilidad": "inmediata/parcial/especificar o vacio",
  "idiomas": "lista de idiomas o vacio",
  "carnet_conducir": "si/no/vacio",
  "vehiculo_propio": "si/no/vacio"
}
Reglas: Si un dato no aparece, pon string vacio. NUNCA inventes datos. SOLO JSON.`
        },
        { role: 'user', content: userContent }
      ],
      max_tokens: 600,
      temperature: 0
    },
    json: true,
    timeout: 30000
  });

  const raw = aiResp.choices?.[0]?.message?.content || '{}';
  cv = JSON.parse(raw.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim());
} catch (e) {
  console.log('AI extraction failed:', e.message);
  cv = { nombre_completo: rutaData.nombreArchivo || '' };
}

// --- 3. Preparar fila para Google Sheet ---
const mesMap = {
  '1. ENERO': 'Enero', '2. FEBRERO': 'Febrero', '3. MARZO': 'Marzo',
  '4. ABRIL': 'Abril', '5. MAYO': 'Mayo', '6. JUNIO': 'Junio',
  '7. JULIO': 'Julio', '8. AGOSTO': 'Agosto', '9. SEPTIEMBRE': 'Septiembre',
  '10. OCTUBRE': 'Octubre', '11. NOVIEMBRE': 'Noviembre', '12. DICIEMBRE': 'Diciembre'
};

const nombre = (cv.nombre_completo && cv.nombre_completo.length > 3)
  ? cv.nombre_completo
  : (rutaData.nombreArchivo || 'Desconocido');

return [{
  json: {
    MES: mesMap[rutaData.mes] || rutaData.mes || '',
    NOMBRE: nombre,
    FORMATO: '',
    OFICINA: rutaData.ciudad || '',
    ' RESIDENCIA': cv.residencia || '',
    FECHA: new Date().toLocaleDateString('es-ES'),
    HORA: '',
    CORREO: cv.email || '',
    'TELÉFONO': cv.telefono || '',
    FUENTE: 'InfoJobs',
    'CONFIRMACIÓN': '',
    'CITADO POR': '',
    ASISTENCIA: '',
    'ENTREVISTADO POR': '',
    'MOTIVO DESCARTE': '',
    'MAIL DESCARTE': '',
    COMENTARIOS: [
      cv.experiencia_resumen ? 'Exp: ' + cv.experiencia_resumen : '',
      cv.formacion ? 'Form: ' + cv.formacion : '',
      cv.idiomas ? 'Idiomas: ' + cv.idiomas : '',
      cv.edad ? 'Edad: ' + cv.edad : '',
      cv.carnet_conducir === 'si' ? 'Carnet: Si' : '',
      cv.vehiculo_propio === 'si' ? 'Vehiculo: Si' : ''
    ].filter(Boolean).join(' | '),
    'SITUACIÓN': rutaData.estado || '',
    'POSICIÓN': rutaData.posicion || '',
    INICIO: '',
    FIN: '',
    CAUSA: '',
    _accion: 'INSERTAR',
    _ultimo_sync: rutaData.estado || '',
    _fileId: rutaData.fileId || '',
    _fileName: rutaData.fileName || ''
  }
}];

// ══════════════════════════════════════════════════════
// MOVER ARCHIVO EN ONEDRIVE
// Cuando la Sheet cambia SITUACION, mover el PDF a la carpeta correcta
// ══════════════════════════════════════════════════════

const item = $input.first().json;

// Mapear estado a nombre de subcarpeta en OneDrive
const CARPETA_MAP = {
  'CITADO': 'CITADOS',
  'DESCARTADO': 'DESCARTADOS',
  'CITA_FUERA_JORNADA': 'CITAS FUERA JORNADA',
  'PENDIENTE': '' // raiz de la semana
};

const nuevoEstado = (item.nuevoEstado || '').toUpperCase().trim();

// Normalizar estado
let estadoNorm = nuevoEstado;
if (nuevoEstado.includes('CITADO') && !nuevoEstado.includes('DESCARTADO') && !nuevoEstado.includes('FUERA')) {
  estadoNorm = 'CITADO';
} else if (nuevoEstado.includes('DESCARTADO') || nuevoEstado.includes('DESCARTE')) {
  estadoNorm = 'DESCARTADO';
} else if (nuevoEstado.includes('FUERA') || nuevoEstado.includes('CITA_FUERA')) {
  estadoNorm = 'CITA_FUERA_JORNADA';
} else {
  estadoNorm = 'PENDIENTE';
}

const subcarpeta = CARPETA_MAP[estadoNorm] || '';

// Construir nueva ruta destino
// Formato: /drive/root:/Base de datos/2026/POSICION/CIUDAD/MES/SEMANA/[SUBCARPETA]
const basePath = '/drive/root:/Base de datos/2026/' + item.posicion + '/' + item.ciudad + '/' + item.mes + '/' + item.semana;
const destPath = subcarpeta ? basePath + '/' + subcarpeta : basePath;

// Mover archivo via Graph API
try {
  const moveResp = await this.helpers.requestWithAuthentication.call(
    this, 'microsoftOneDriveOAuth2Api', {
      method: 'PATCH',
      uri: 'https://graph.microsoft.com/v1.0/me/drive/items/' + item.fileId,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        parentReference: {
          path: destPath
        }
      })
    }
  );

  return [{ json: {
    NOMBRE: item.matchNombre || item.nombreArchivo,
    _ultimo_sync: estadoNorm,
    _accion: 'MOVER_ONEDRIVE',
    _situacionAnterior: item.estado,
    _situacionNueva: estadoNorm,
    _fileName: item.fileName,
    _destino: destPath,
    _movido: true
  }}];
} catch (e) {
  return [{ json: {
    NOMBRE: item.matchNombre || item.nombreArchivo,
    _accion: 'MOVER_ONEDRIVE',
    _error: e.message,
    _fileName: item.fileName,
    _destino: destPath,
    _movido: false
  }}];
}
